p={--player
	hl=3,
}

scr=0--score

function TIC()
	render()
end

function render()
	map()
	hud()
end

function hud()
	h=p.hl*8
	--bg
	rect(0,0,240,14,0)
	--hati
 for x=8,24,8 do spr(177,x,2,0) end
 for x=8,h,8 do spr(176,x,2,0) end
 --skor
 print("SCORE : "..scr.."",160,3,12)
end
